import React, { useState } from "react";
import Work1 from "../../assets/my_portfolio.jpg";
import Work2 from "../../assets/work2.jpg";
import Work3 from "../../assets/work3.jpg";
import Work4 from "../../assets/work4.jpg";
import Work5 from "../../assets/work5.jpg";
import Work6 from "../../assets/Call_center.jpg";
import Work7 from "../../assets/energy_consumption.jpg";
import Work8 from "../../assets/Car_booking_chatbot.jpg";

// Project data
export const projectsData = [
  {
    id: 1,
    image: Work1,
    title: "Portfolios",
    category: "web",
    link: "#", // No link for this project
  },
  {
    id: 2,
    image: Work6,
    title: "Call Center Analysis",
    category: "Analysis",
    link: "https://github.com/Pivink/Data-Analysis/tree/main/Call%20Centre",
  },
  {
    id: 3,
    image: Work8,
    title: "Car Booking Chatbot",
    category: "design",
    link: "#", // No link for this project
  },
  {
    id: 4,
    image: Work7,
    title: "Energy Consumption Analysis",
    category: "Analysis",
    link: "https://www.novypro.com/project/energy-consumption-2",
  },
  {
    id: 5,
    image: Work5,
    title: "Web design",
    category: "web",
    link: "#", // No link for this project
  },
];

const Portfolio = () => {
  const [toggleState, setToggleState] = useState(0); // State to control which modal is open

  const toggleTab = (index) => {
    setToggleState(index);
  };

  return (
    <div className="portfolio">
      {projectsData.map((project) => (
        <div key={project.id} className="portfolio__item">
          <img src={project.image} alt={project.title} className="portfolio__image" />
          <h3 className="portfolio__title">{project.title}</h3>
          <span className="portfolio__category">{project.category}</span>

          {/* Conditionally render View Demo button or View More button */}
          {project.link && project.link !== "#" ? (
            <a
              href={project.link}
              className="portfolio__button"
              target="_blank"
              rel="noopener noreferrer"
            >
              View Demo
            </a>
          ) : (
            <span
              className="portfolio__button"
              onClick={() => toggleTab(project.id)}
            >
              View More
            </span>
          )}

          {/* Modal for demo */}
          <div
            className={toggleState === project.id ? "modal active-modal" : "modal"}
          >
            <div className="modal__content">
              <i
                onClick={() => setToggleState(0)}
                className="uil uil-times modal__close"
              ></i>
              <h3 className="modal__title">{project.title}</h3>
              <p className="modal__description">
                {/* Here you can add a description or project details */}
                Detailed project information goes here. You can add any relevant
                details about the project, like the technologies used, scope,
                goals, or outcomes.
              </p>

              <ul className="modal__services">
                <li>Responsive web pages</li>
                <li>User interface design</li>
                <li>Brand identity alignment</li>
                <li>Mockups for business websites</li>
              </ul>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Portfolio;
